import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, Platform, TouchableOpacity } from 'react-native';
import { Header, styles, Logo, Menu, Page, Search, SearchInput, Botao, OrganizationsList, Item, RepoTitle } from './styled';


export default function App() {


  const [searchRepositorie, setSearchRepositorie] = useState('');

  const [repositories, setRepositories] = useState([]);



  useEffect(() => {

    async function getRepositories() {

      const req = await fetch("https://api.github.com/users/matheuskeidygomes/repos");
      const json = await req.json();

      if (json) {
        setRepositories(json);
      }

    }

    getRepositories();

  }, []);



  return (

    <SafeAreaView style={styles.container}>

        <Header>

          <Logo>

            <Image
              source={require('./assets/logotipo.png')} style={{ width: 100, height: 70 }} resizeMode="stretch"
            />

          </Logo>

          <Menu>

            <Image
            source={require('./assets/cardapio.png')} style={{ width: 40, height: 40 }} resizeMode="cover"
          />

        </Menu>


      </Header>

      <Page>

        <Search>

          <SearchInput value={searchRepositorie} onChangeText={e => setSearchRepositorie(e)} />

          <TouchableOpacity style={styles.button} onPress={() => { }} >
            <Image
              source={require('./assets/pesquisa.svg')} style={{ width: 50, height: 50 }} resizeMode="cover"
            />
          </TouchableOpacity>

        </Search>

        <OrganizationsList>

          {repositories.map((item, index) => (

            <Item key={index} data={item}>

              <RepoTitle> {item.name} </RepoTitle>

              <RepoTitle> Local:{item.full_name} </RepoTitle>

              <RepoTitle> Author: {item.owner.login} </RepoTitle>



            </Item>



          ))}



        </OrganizationsList>

      </Page>


    </SafeAreaView>



  )

}
