import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, Platform, TouchableOpacity, Linking, FlatList } from 'react-native';
import { Header, styles, Logo, Menu, Page, Search, SearchInput, OrganizationsList, Item, ItemText } from './styled';


export default function App() {


  const [searchText, setSearchText] = useState('');

  const [repositories, setRepositories] = useState([]);



  useEffect(() => {

    async function getRepositories() {

      const req = await fetch("https://api.github.com/users/matheuskeidygomes/repos");

      const json = await req.json();

      if (json) {

        setRepositories(json);

      }

    }

    getRepositories();

  }, []);



  useEffect(() => {

    if (searchText != '') {

      setRepositories(repositories.filter((item) => item.name.toLowerCase().indexOf(searchText.toLowerCase()) > -1));

    } else {

      setRepositories(repositories);

    }

  }, [searchText]);




  useEffect(() => {

    if (searchText === '') {

      setRepositories(repositories.filter((item) => item.name.toLowerCase().indexOf(searchText.toLowerCase()) > -1));

    } else {

      setRepositories(repositories);

    }

  }, [searchText]);




  return (

    <SafeAreaView style={styles.container}>

      <Header>

        <Logo>

          <Image
            source={require('./assets/logotipo.png')} style={{ width: 100, height: 70 }} resizeMode="stretch"
          />

        </Logo>

        <Menu>

          <Image
            source={require('./assets/cardapio.png')} style={{ width: 40, height: 40 }} resizeMode="cover"
          />

        </Menu>

      </Header>


      <Page>

        <Search>

          <SearchInput value={searchText} onChangeText={e => setSearchText(e)} placeholder="Pesquisar pelo nome do repositório" />

          <TouchableOpacity style={styles.button}  >
            <Image
              source={require('./assets/search.png')} style={{ width: 40, height: 40 }} resizeMode="cover"
            />
          </TouchableOpacity>

        </Search>

        <OrganizationsList>



          <FlatList

            data={repositories}
            renderItem={({ item }) =>

              <Item data={item} >

                <ItemText> <Text style={{ fontWeight: 'bold', color: 'rgb(18,123,249)' }}>Repository Name:</Text> {item.name} </ItemText>

                <ItemText> <Text style={{ fontWeight: 'bold', color: 'rgb(18,123,249)' }}>Repository Local:</Text> {item.full_name} </ItemText>

                <ItemText> <Text style={{ fontWeight: 'bold', color: 'rgb(18,123,249)' }}>Author:</Text> {item.owner.login} </ItemText>

                <ItemText> <Text style={{ fontWeight: 'bold', color: 'rgb(18,123,249)' }}>About:</Text> {item.description} </ItemText>

                <TouchableOpacity style={styles.button2} onPress={() => Linking.openURL(item.html_url)} >

                  <ItemText> <Text style={{ fontWeight: 'bold' }}> Go to repository </Text> </ItemText>

                </TouchableOpacity>


              </Item>

            }
            keyExtractor={(item) => item.id}

          />


        </OrganizationsList>

      </Page>


    </SafeAreaView>



  )

}
