import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, Platform } from 'react-native';
import styled from 'styled-components/native';
import style from 'styled-components';



const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Platform.OS === 'android' ? 36 : 0
  },
});



const Header = styled.View` 

   flex-direction:row;
   justify-content:space-between;
   align-items:center;
   background-color:gray;
   padding:20px;

`;


const Logo = styled.View` 

justify-content:center;

`;


const Menu = styled.View`

padding:10px;
justify-content:center;

`


const Page = styled.View` 

   flex:1;
   background-color:black;


`;

const Search = styled.View`
padding:20px;
flex-direction:row;

`;

const SearchInput = styled.TextInput`
height:50px;
color:white;
background-color:white;
flex:1;
border:0;
border-radius:5px;
outline:0;

`;


const Botao = styled.Button`
border-radius:5px;
background-color:blue;
border:0;
width:50px;
height:50px;
`;


const OrganizationsList = styled.View`
padding:20px;

`;

const Item = styled.View`

margin-top:10px;
margin-bottom:10px;
border:1px solid white;
border-radius:5px;
padding:10px;
height:100px;

`;


const RepoTitle = styled.Text`

color:white;

`;




export default function App() {

  const [searchRepositorie, setSearchRepositorie] = useState('');

  const [repositories, setRepositories] = useState([]);


  useEffect(() => {

    async function getRepositories() {

      const req = await fetch("https://api.github.com/users/matheuskeidygomes/repos");
      const json = await req.json();

      if (json) {
        setRepositories(json);
      }

    }

    getRepositories();

  }, []);




  return (


    <SafeAreaView style={styles.container}>

      <Header>

        <Logo>

          <Image
            source={require('./assets/logotipo.png')} style={{ width: 100, height: 70 }} resizeMode="stretch"
          />

        </Logo>

        <Menu>

          <Image
            source={require('./assets/cardapio.png')} style={{ width: 40, height: 40 }} resizeMode="cover"
          />

        </Menu>


      </Header>

      <Page>

        <Search>

          <SearchInput value={searchRepositorie} onChangeText={e => setSearchRepositorie(e)} />

          <Botao/>

        </Search>

        <OrganizationsList>

          {repositories.map((item, index) => (

            <Item key={index} data={item}>

              <RepoTitle> {item.name} </RepoTitle>

              <RepoTitle> Local: {item.full_name} </RepoTitle>

              <RepoTitle> Author: {item.owner.login} </RepoTitle>

              <RepoTitle> About: {item.description} </RepoTitle>



            </Item>



          ))}



        </OrganizationsList>

      </Page>


    </SafeAreaView>



  )

}
