import { StatusBar } from 'expo-status-bar';
import React, { useState, useEffect } from 'react';
import { StyleSheet, Text, View, Image, SafeAreaView, Platform, TouchableOpacity } from 'react-native';
import styled from 'styled-components/native';



const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Platform.OS === 'android' ? 36 : 0
  },
  button: {
    color: 'white',
    height: 50,
    width:50,
    backgroundColor: 'rgb(18,123,249)',
    borderRadius: 5,
    marginLeft:5
  }
});





const Header = styled.View` 

   flex-direction:row;
   justify-content:space-between;
   align-items:center;
   background-color:gray;
   padding:20px;

`;


const Logo = styled.View` 

justify-content:center;

`;


const Menu = styled.View`

padding:10px;
justify-content:center;

`


const Page = styled.View` 

   flex:1;
   background-color:black;


`;

const Search = styled.View`
padding:20px;
flex-direction:row;

`;

const SearchInput = styled.TextInput`
height:50px;
color:white;
background-color:white;
flex:1;
border:0;
border-radius:5px;

`;


const Botao = styled.Button`



`;


const OrganizationsList = styled.View`
padding:20px;

`;

const Item = styled.View`

margin-top:10px;
margin-bottom:10px;
border:1px solid white;
border-radius:5px;
padding:10px;
height:100px;

`;


const RepoTitle = styled.Text`

color:white;

`;




export default function App() {

  const [searchRepositorie, setSearchRepositorie] = useState('');

  const [repositories, setRepositories] = useState([]);


  useEffect(() => {

    async function getRepositories() {

      const req = await fetch("https://api.github.com/users/matheuskeidygomes/repos");
      const json = await req.json();

      if (json) {
        setRepositories(json);
      }

    }

    getRepositories();

  }, []);




  return (


    <SafeAreaView style={styles.container}>

      <Header>

        <Logo>

          <Image
            source={require('./assets/logotipo.png')} style={{ width: 100, height: 70 }} resizeMode="stretch"
          />

        </Logo>

        <Menu>

          <Image
            source={require('./assets/cardapio.png')} style={{ width: 40, height: 40 }} resizeMode="cover"
          />

        </Menu>


      </Header>

      <Page>

        <Search>

          <SearchInput value={searchRepositorie} onChangeText={e => setSearchRepositorie(e)} />

          <TouchableOpacity style={styles.button} onPress={() => { }} >
            <Image
              source={require('./assets/pesquisa.svg')} style={{ width: 50, height: 50 }} resizeMode="cover"
            />
          </TouchableOpacity>

        </Search>

        <OrganizationsList>

          {repositories.map((item, index) => (

            <Item key={index} data={item}>

              <RepoTitle> {item.name} </RepoTitle>

              <RepoTitle> Local:{item.full_name} </RepoTitle>

              <RepoTitle> Author: {item.owner.login} </RepoTitle>



            </Item>



          ))}



        </OrganizationsList>

      </Page>


    </SafeAreaView>



  )

}
